import Airtable from 'airtable';

const base = new Airtable({
    apiKey: process.env.REACT_APP_AIRTABLE_API_KEY
}).base('appuojNVDfs9U7ccy');

export const fetchAirtableData = async () => {
    try {
        const records = await base('tbl60mtZmcPavvtQH').select().all();
        const data = records.map(record => record.fields);

        // Process the data to get ranges and categories
        const processedData = {
            phases: [...new Set(data.map(item => Number(item.Phase) || 0))],
            divisions: [...new Set(data.map(item => Number(item.Division) || 0))]
                .filter(Boolean)
                .sort((a, b) => a - b)  // Numeric sort
                .map(div => ({
                    value: div,
                    label: `Division ${div}`  // Add "Division" prefix for clarity
                })),
            wbsCategories: [...new Set(data.map(item => item['WBS Category Level 1']))].filter(Boolean).sort().map(cat => ({
                value: cat,
                label: cat
            })),
            durations: data.map(item => Number(item.Duration) || 0),
            startDates: data.map(item => new Date(item['Start Date'])),
            endDates: data.map(item => new Date(item['End Date']))
        };

        // Get min and max dates
        const validStartDates = processedData.startDates.filter(date => date instanceof Date && !isNaN(date));
        const validEndDates = processedData.endDates.filter(date => date instanceof Date && !isNaN(date));

        return {
            raw: data,
            processed: processedData,
            ranges: {
                phase: [Math.min(...processedData.phases), Math.max(...processedData.phases)],
                duration: [Math.min(...processedData.durations), Math.max(...processedData.durations)],
                dates: {
                    min: validStartDates.length ? new Date(Math.min(...validStartDates)) : null,
                    max: validEndDates.length ? new Date(Math.max(...validEndDates)) : null
                }
            }
        };
    } catch (error) {
        console.error('Error fetching Airtable data:', error);
        return null;
    }
};
