export const lightTheme = {
    body: 'linear-gradient(165deg, #f5f5f5 0%, #e0e0e0 100%)',
    text: '#2a2a2a',
    accent: '#00a8e8',
    cardBg: 'rgba(255, 255, 255, 0.9)',
    cardBorder: 'rgba(0, 168, 232, 0.2)',
    shadowColor: 'rgba(0, 168, 232, 0.3)',
    sliderTrackBg: 'rgba(0, 168, 232, 0.1)',
};

export const darkTheme = {
    body: 'linear-gradient(165deg, #1a1a1a 0%, #0d2337 100%)',
    text: '#00fff2',
    accent: '#00fff2',
    cardBg: 'rgba(0, 255, 242, 90)',
    cardBorder: 'rgba(0, 255, 242, 0.2)',
    shadowColor: 'rgba(0, 255, 242, 0.3)',
    sliderTrackBg: 'rgba(0, 255, 242, 0.1)',
};
